jQuery(document).ready(function($){$(".ask-me-anything-button").click(function(e){e.preventDefault();var targetID=$(this).data("target");AskMeAnythingLaunchModal(targetID)});$(document).mouseup(function(e){var container=$(".ask-me-anything-modal-inner");if(!container.is(e.target)&&container.has(e.target).length===0){AskMeAnythingCloseModal("#ask-me-anything")}});document.addEventListener("keydown",function(ev){var keyCode=ev.keyCode||ev.which;if(keyCode===27){AskMeAnythingCloseModal("#ask-me-anything")}});function AskMeAnythingLaunchModal(targetID){if(targetID==""||typeof targetID==="undefined"){return false}$("body").append('<div id="ask-me-anything-backdrop"></div>');$("#ask-me-anything-backdrop").addClass("ama-in");$(targetID).show().addClass("ama-in");if(ASK_ME_ANYTHING.display_questions==true){AskMeAnythingGetQuestions(1)}}function AskMeAnythingCloseModal(targetID){if(targetID==""||typeof targetID==="undefined"){return false}$(targetID).hide();$("#ask-me-anything-backdrop").remove()}function AskMeAnythingGetQuestions(page){var amaQuestionTemplate=wp.template("ama-question");var questionsList=$(".ask-me-anything-questions-list");questionsList.empty().append('<div style="text-align: center; padding: 1em;"><i class="fa fa-spinner fa-spin fa-3x"></i></div>');var data={action:"ask_me_anything_get_questions",page_number:page,nonce:ASK_ME_ANYTHING.nonce};$.post(ASK_ME_ANYTHING.ajaxurl,data,function(response){if(response.success==true){questionsList.empty().append(amaQuestionTemplate({questions:response.data}))}else{console.log(response)}})}});